mapper.select:
select t_yx_company.name as company_name, t_yx_demand.*
from t_yx_demand left join t_yx_company on t_yx_demand.company_id = t_yx_company.id

	t_yx_demand.id <= (select id from t_yx_demand order by t_yx_demand.id desc limit #{offset,jdbcType=INTEGER},1)
	
		and t_yx_demand.company_id = #{companyId,jdbcType=INTEGER}
	
	
		and t_yx_demand.demand_number like #{demandNumber,jdbcType=VARCHAR}
	
	
		and t_yx_company.name like #{companyName,jdbcType=VARCHAR}
	
	
		and t_yx_demand.state = #{state,jdbcType=INTEGER}
	
	
		and t_yx_demand.create_time >= #{createBeginTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.create_time <= #{createEndTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.close_time >= #{closeBeginTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.close_time <= #{closeEndTime,jdbcType=TIMESTAMP}
	

order by t_yx_demand.id desc limit #{pageSize,jdbcType=INTEGER}

select count(t_yx_demand.id) from t_yx_demand left join t_yx_company on t_yx_demand.company_id = t_yx_company.id

	
		and t_yx_demand.company_id = #{companyId,jdbcType=INTEGER}
	
	
		and t_yx_company.name like #{companyName,jdbcType=VARCHAR}
	
	
		and t_yx_demand.demand_number like #{demandNumber,jdbcType=VARCHAR}
	
	
		and t_yx_demand.state = #{state,jdbcType=INTEGER}
	
	
		and t_yx_demand.create_time >= #{createBeginTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.create_time <= #{createEndTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.close_time >= #{closeBeginTime,jdbcType=TIMESTAMP}
	
	
		and t_yx_demand.close_time <= #{closeEndTime,jdbcType=TIMESTAMP}


  select count(id) from t_yx_demand
  
  	
  		and company_id = #{companyId,jdbcType=INTEGER}
  	
  	
  		and create_time >= #{createTime,jdbcType=TIMESTAMP}

  	select count(1) from t_yx_demand t 
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

	SELECT
		c.NAME company_name,
		taker_u.real_name undertake_user_name,
		close_u.real_name close_user_name,
		create_u.real_name create_user_name,
		(case when d.state = 0 then '待处理' when d.state = 1 then '处理中' when d.state = 2 then '已签约' when d.state = 3 then '已结束' end) state_name,
		d.id, d.demand_number, d.company_id, d.state, d.description, d.other_treatment, d.undertake_time, 
	    d.undertake_user, d.close_time, d.close_status, d.close_user, d.close_reason, d.create_user, 
	    d.create_time
	FROM
		t_yx_demand d
		LEFT JOIN t_yx_company c ON d.company_id = c.id
		LEFT JOIN t_sys_user taker_u ON d.undertake_user = taker_u.id
		LEFT JOIN t_sys_user close_u ON d.close_user = close_u.id
		LEFT JOIN t_sys_user create_u ON d.create_user = create_u.id
	
		d.id <= (select t.id from t_yx_demand t order by t.id desc limit #{offset,jdbcType=INTEGER},1)
		
    		and c.name like #{companyName,jdbcType=VARCHAR}
    	
		
    		and taker_u.real_name like #{undertakeUserName,jdbcType=VARCHAR}
    	
		
    		and close_u.real_name like #{closeUserName,jdbcType=VARCHAR}
    	
    	
    		and d.demand_number like #{demandNumber,jdbcType=VARCHAR}
    	
    	
    		and d.state = #{state,jdbcType=INTEGER}
    	
    	
    		and d.create_time >= #{beginTime,jdbcType=TIMESTAMP}
    	
    	
    		and d.create_time <= #{endTime,jdbcType=TIMESTAMP}
    	
	 
	order by d.id desc limit #{pageSize,jdbcType=INTEGER}

  	SELECT count(1)
	FROM
		t_yx_demand d
		LEFT JOIN t_yx_company c ON d.company_id = c.id
		LEFT JOIN t_sys_user taker_u ON d.undertake_user = taker_u.id
		LEFT JOIN t_sys_user close_u ON d.close_user = close_u.id
		LEFT JOIN t_sys_user create_u ON d.create_user = create_u.id
	
		
    		and c.name like #{companyName,jdbcType=VARCHAR}
    	
		
    		and taker_u.real_name like #{undertakeUserName,jdbcType=VARCHAR}
    	
		
    		and close_u.real_name like #{closeUserName,jdbcType=VARCHAR}
    	
    	
    		and d.demand_number like #{demandNumber,jdbcType=VARCHAR}
    	
    	
    		and d.state = #{state,jdbcType=INTEGER}
    	
    	
    		and d.create_time >= #{beginTime,jdbcType=TIMESTAMP}
    	
    	
    		and d.create_time <= #{endTime,jdbcType=TIMESTAMP}
