mapper.insert:

insert into t_yx_demand_order (id, demand_id, order_number, 
  worker_count, operator_user, customer, 
  total_income, description, create_user, 
  create_time, update_time, update_user
  )
values (#{id,jdbcType=INTEGER}, #{demandId,jdbcType=INTEGER}, #{orderNumber,jdbcType=VARCHAR}, 
  #{workerCount,jdbcType=INTEGER}, #{operatorUser,jdbcType=INTEGER}, #{customer,jdbcType=VARCHAR}, 
  #{totalIncome,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{createUser,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=INTEGER}
  )

mapper.select:
  	select count(1) from t_yx_demand_order t 
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

  	select sum(worker_count) from t_yx_demand_order t 
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

  	select sum(cast(t.total_income as  decimal(10,2))) from t_yx_demand_order t
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

  	SELECT
		x.id, x.demand_id, x.order_number, x.worker_count, x.operator_user, x.customer, x.total_income, 
    	x.description, x.create_user, x.create_time, x.update_time, x.update_user, x.company_name,
		u.real_name create_user_name 
	FROM
		(
	SELECT
		o.id, o.demand_id, o.order_number, o.worker_count, o.operator_user, o.customer, o.total_income, 
    	o.description, o.create_user, o.create_time, o.update_time, o.update_user,
		c.NAME company_name 
	FROM
		t_yx_demand d,
		t_yx_company c,
		t_yx_demand_order o 
	WHERE
		d.company_id = c.id 
		AND o.demand_id = d.id 
		) x
		LEFT JOIN t_sys_user u ON x.create_user = u.id 
	
		x.id <= (select m.id from t_yx_demand_order m, t_sys_user n 
			
				
					and m.create_time >= #{beginTime,jdbcType=TIMESTAMP}
				
				
					and m.create_time <= #{endTime,jdbcType=TIMESTAMP}
				
				
					and m.company_name like #{companyName,jdbcType=VARCHAR}
				
				
					and m.order_number = #{orderNumber,jdbcType=VARCHAR}
				
				
					and n.real_name like #{createUserName,jdbcType=VARCHAR}
				
			
		 order by m.id desc limit #{offset,jdbcType=INTEGER},1)
  		
			and x.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and x.create_time <= #{endTime,jdbcType=TIMESTAMP}
		
		
			and x.company_name like #{companyName,jdbcType=VARCHAR}
		
		
			and x.order_number = #{orderNumber,jdbcType=VARCHAR}
		
		
			and u.real_name like #{createUserName,jdbcType=VARCHAR}
		
  	
  	order by x.id desc limit #{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM ( SELECT o.id, o.demand_id, o.order_number, o.worker_count, o.operator_user, o.customer, o.total_income, o.description, o.create_user, o.create_time, o.update_time, o.update_user, c.NAME company_name FROM t_yx_demand d, t_yx_company c, t_yx_demand_order o WHERE d.company_id = c.id AND o.demand_id = d.id ) x LEFT JOIN t_sys_user u ON x.create_user = u.id
and x.create_time >= #{beginTime,jdbcType=TIMESTAMP}
and x.create_time <= #{endTime,jdbcType=TIMESTAMP}
and x.company_name like #{companyName,jdbcType=VARCHAR}
and x.order_number = #{orderNumber,jdbcType=VARCHAR}
and u.real_name like #{createUserName,jdbcType=VARCHAR}

SELECT c.NAME companyName, count( 0 ) orderCount, sum( o.worker_count ) orderMemberCount, sum( cast( o.total_income AS DECIMAL ( 10, 2 ) ) ) orderIncome FROM t_yx_demand d, t_yx_demand_order o, t_yx_company c WHERE d.id = o.demand_id AND d.company_id = c.id
and o.create_time >= #{beginTime,jdbcType=TIMESTAMP}
and o.create_time <= #{endTime,jdbcType=TIMESTAMP}
and c.name like #{companyName,jdbcType=VARCHAR}
GROUP BY c.NAME
