mapper.select:
  	select count(1) from t_yx_demand_order t 
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

  	select sum(worker_count) from t_yx_demand_order t 
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

  	select sum(cast(t.total_income as  decimal(10,2))) from t_yx_demand_order t
  	
  		
			and t.create_time >= #{beginTime,jdbcType=TIMESTAMP}
		
		
			and t.create_time <= #{endTime,jdbcType=TIMESTAMP}

SELECT x.id, x.demand_id, x.order_number, x.worker_count, x.operator_user, x.customer, x.total_income, x.description, x.create_user, x.create_time, x.update_time, x.update_user, x.company_name, u.real_name create_user_name FROM ( SELECT o.id, o.demand_id, o.order_number, o.worker_count, o.operator_user, o.customer, o.total_income, o.description, o.create_user, o.create_time, o.update_time, o.update_user, c.NAME company_name FROM t_yx_demand d, t_yx_company c, t_yx_demand_order o WHERE d.company_id = c.id AND o.demand_id = d.id ) x LEFT JOIN t_sys_user u ON x.create_user = u.id
x.id <= (select id from t_yx_demand_order order by id desc limit #{offset,jdbcType=INTEGER},1)
and x.create_time >= #{beginTime,jdbcType=TIMESTAMP}
and x.create_time <= #{endTime,jdbcType=TIMESTAMP}
and x.company_name like #{companyName,jdbcType=VARCHAR}
and x.order_number = #{orderNumber,jdbcType=VARCHAR}
and u.real_name like #{createUserName,jdbcType=VARCHAR}
order by x.id desc limit #{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM ( SELECT o.id, o.demand_id, o.order_number, o.worker_count, o.operator_user, o.customer, o.total_income, o.description, o.create_user, o.create_time, o.update_time, o.update_user, c.NAME company_name FROM t_yx_demand d, t_yx_company c, t_yx_demand_order o WHERE d.company_id = c.id AND o.demand_id = d.id ) x LEFT JOIN t_sys_user u ON x.create_user = u.id
and x.create_time >= #{beginTime,jdbcType=TIMESTAMP}
and x.create_time <= #{endTime,jdbcType=TIMESTAMP}
and x.company_name like #{companyName,jdbcType=VARCHAR}
and x.order_number = #{orderNumber,jdbcType=VARCHAR}
and u.real_name like #{createUserName,jdbcType=VARCHAR}
