mapper.select:
  	SELECT
		t.id, t.name, t.photo, t.sex, t.telephone, t.idcard, t.birthday, t.birthplace_code, t.work_year, t.marital_status, 
		t.workplace_code, t.expect_salary, t.work_status, t.nation, t.position, t.address, t.language_level, 
		t.work_expect, t.email, t.title, t.night_work, t.souce, t.create_time, t.create_user, t.update_time, t.update_user,
		u.real_name 
	FROM
		t_yx_worker t,t_sys_user u 

		t.create_user = u.id
		and t.id > #{offset,jdbcType=INTEGER}

			and t.idcard = #{idcard,jdbcType=VARCHAR}


			and t.phone = #{telephone,jdbcType=VARCHAR}


			and t.name like #{name,jdbcType=VARCHAR}


			and t.create_time >= #{beginTime,jdbcType=VARCHAR}


			and t.create_time <= #{endTime,jdbcType=VARCHAR}


			and t.source = #{source,jdbcType=INTEGER}


			and t.create_user = #{createUser,jdbcType=INTEGER}


			AND EXISTS ( 
				SELECT 1 FROM t_yx__worker_jobtype j WHERE t.id = j.worker_id 
				AND j.first_id = #{firstId,jdbcType=INTEGER}

					AND j.second_id = #{secondId,jdbcType=INTEGER}

			)


	order by id desc limit #{pageSize,jdbcType=INTEGER}

  	SELECT count(1) FROM t_yx_worker t,t_sys_user u 

		t.create_user = u.id
		and t.id > #{offset,jdbcType=INTEGER}

			and t.idcard = #{idcard,jdbcType=VARCHAR}


			and t.phone = #{telephone,jdbcType=VARCHAR}


			and t.name like #{name,jdbcType=VARCHAR}


			and t.create_time >= #{beginTime,jdbcType=VARCHAR}


			and t.create_time <= #{endTime,jdbcType=VARCHAR}


			and t.source = #{source,jdbcType=INTEGER}


			and t.create_user = #{createUser,jdbcType=INTEGER}


			AND EXISTS ( 
				SELECT 1 FROM t_yx__worker_jobtype j WHERE t.id = j.worker_id 
				AND j.first_id = #{firstId,jdbcType=INTEGER}

					AND j.second_id = #{secondId,jdbcType=INTEGER}

			)
